import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: this.props.activeItem,
    };
  }

  handleChange = (e) => {
    let { name, value } = e.target;

    if (e.target.type === "checkbox") {
      value = e.target.checked;
    }

    const activeItem = { ...this.state.activeItem, [name]: value };

    this.setState({ activeItem });
  };

  render() {
    const { toggle, onSave } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Recipe Entry</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="recipe-name">Name</Label>
              <Input
                type="text"
                id="recipe-name"
                name="title"
                value={this.state.activeItem.name}
                onChange={this.handleChange}
                placeholder="Enter Recipe Name"
              />
            </FormGroup>
            <FormGroup>
              <Label for="recipe-ingredients">Ingredients</Label>
              <Input
                type="text"
                id="recipe-ingredients"
                name="ingredients"
                value={this.state.activeItem.ingredients}
                onChange={this.handleChange}
                placeholder="Enter Recipe Ingredients"
              />
            </FormGroup>
            <FormGroup check>
              <Label check>
                <Input
                  type="checkbox"
                  name="favorite"
                  checked={this.state.activeItem.favorite}
                  onChange={this.handleChange}
                />
                Favorite
              </Label>
            </FormGroup>
            <FormGroup>
              <Label for="recipe-time-req-minutes">Time to Make in Minutes</Label>
              <Input
                type="number"
                id="recipe-time-req-minutes"
                name="time-req-minutes"
                value={this.state.activeItem.time_req_minutes}
                onChange={this.handleChange}
                placeholder="Enter Required time to make"
              />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button
            color="success"
            onClick={() => onSave(this.state.activeItem)}
          >
            Save
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
